/**
  * @brief Get string size
  * @param str - string
  * @param sizeStruct - font size structure
  * @param font - font
  * @retval void
 */
char* st7789_symbols_getStringSize(char* str, fonts_size_t* sizeStruct, font_t* font) {
	sizeStruct->str_height = font->fontHeight;
	sizeStruct->str_length = font->fontWidth * strlen(str);
	return str;
}

